<?phpinclude_once('Controller.php');abstract class Controllers_Base extends Controller{	protected $title;	protected $content;    protected $login;    protected $logout;	function __construct()	{        $this->title = 'Приложение задачник';        $this->login = 'Вход';        $this->logout = '';        $this->content = '';        if(isset($_SESSION['user'])){            $this->login = '';            $this->logout = 'Выход';        }	}		public function render()	{		$vars = array(		    'title' => $this->title,            'login' => $this->login,            'logout' => $this->logout,            'content' => $this->content,        );		$page = $this->Template('views/main.php', $vars);		echo $page;	}	}