<?phprequire_once('db.php');function authWithCredentials(){    $username = $_POST['login'];    $password = $_POST['password'];    try{        $q = "SELECT id, `name`, login, password FROM users WHERE login = '$username'";        $sql = SQL::getInstance()->Select($q);        $row = $sql['0'];        $id = $row['id'];        $login = $row['login'];        $db_password = $row['password'];        $isAuth = 0;        if ($login == $username && $password == $db_password)  {                $isAuth = 1;        }        $_SESSION['user'] = $row;    }    catch(PDOException $e){        die("Error: ".$e->getMessage());    }    return $isAuth;}