<?phprequire_once('db.php');function getUser($id_user){    $id_user = (int)$id_user;    try {        $q = "SELECT * FROM users WHERE id = ".$id_user;        $sql = SQL::getInstance()->Select($q);    }    catch(PDOException $e){        die("Error: ".$e->getMessage());    }    return $sql;}function getUsersData(){    try {        $q = "SELECT * FROM users";        $sql = SQL::getInstance()->Select($q);    } catch (PDOException $e) {        die("Error: " . $e->getMessage());    }    return $sql;};